mapper.update:
UPDATE and_applicant_#{andId}
SET and_role_id = #{andRoleId},
    and_apply_title = #{andApplyTitle},
    and_apply_content = #{andApplyContent},
    and_apply_file = #{andApplyFile}
WHERE and_apply_id = #{andApplyId}

UPDATE and_applicant_#{andId}
SET
    and_apply_status = #{andApplyStatus}
WHERE and_apply_id = #{andApplyId}

mapper.delete:
UPDATE and_applicant_#{andId}
SET is_deleted = true
WHERE and_apply_id = #{andApplyId}
