mapper.insert:
INSERT INTO and_member_#{andId} (
    and_id, user_id, and_apply_id, is_deleted
)
VALUES (
           #{andId}, #{userId}, #{andApplyId}, #{isDeleted}
       )

mapper.update:
UPDATE and_member_#{andId}
SET user_id = #{userId},is_deleted = #{isDeleted}
WHERE member_id = #{memberId}

mapper.delete:
UPDATE
    and_member_#{andId}
SET
    is_deleted = true
WHERE
    member_id = #{memberId}

UPDATE
    and_member_#{andId}
SET
    is_deleted = true
WHERE
    user_id = #{userId}

mapper.insert:
CREATE TABLE and_member_1111 (
member_id INT AUTO_INCREMENT PRIMARY KEY,
and_id INT NOT NULL,
user_id INT NOT NULL,
is_deleted BOOL DEFAULT false
);
