mapper.update:
UPDATE and_qna_#{andId}
SET and_qna_title = #{andQnaTitle},
    and_qna_content = #{andQnaContent}, updated_at = #{updatedAt}, is_deleted = #{isDeleted}
WHERE and_qna_id = #{andQnaId}

mapper.delete:
UPDATE and_qna_#{andId}
SET is_deleted = true
WHERE and_qna_id = #{andQnaId}

UPDATE and_qna_#{andId}
SET is_deleted = true
WHERE and_id = #{andId}
