mapper.update:
UPDATE and_qna_reply_#{andId}
SET
    and_reply_content = #{andReplyContent},
    updated_at = #{updatedAt},
    is_deleted = #{isDeleted}
WHERE and_reply_id = #{andReplyId}

mapper.delete:
UPDATE and_qna_reply_#{andId}
SET is_deleted = true
WHERE and_reply_id = #{andReplyId}

UPDATE and_qna_reply_#{andId}
SET is_deleted = true
WHERE and_qna_id = #{andQnaId}

UPDATE and_qna_reply_#{andId}
SET is_deleted = true
WHERE and_id = #{andId}
