mapper.update:
UPDATE
    crowd_qna_#{crowdId}
SET
    qna_title = #{qnaTitle},
    qna_content = #{qnaContent},
    updated_at = #{updatedAt},
    is_deleted = #{isDeleted}
WHERE
    crowd_qna_id = #{crowdQnaId}

mapper.delete:
UPDATE
    crowd_qna_#{crowdId}
SET
    is_deleted = true
WHERE
    crowd_qna_id = #{crowdQnaId}

UPDATE
    crowd_qna_#{crowdId}
SET
    is_deleted = true
WHERE
    crowd_id = #{crowdId}
