mapper.update:
UPDATE
    crowd_qna_reply_#{crowdId}
SET
    qna_reply_content = #{qnaReplyContent},
    updated_at = #{updatedAt}
    is_deleted = #{isDeleted}
WHERE
    qna_reply_id = #{qnaReplyId}

mapper.delete:
UPDATE
    crowd_qna_reply_#{crowdId}
SET
    is_deleted = true
WHERE
    qna_reply_id = #{qnaReplyId}

UPDATE
    crowd_qna_reply_#{crowdId}
SET
    is_deleted = true
WHERE
    crowd_qna_id = #{crowdQnaId}

UPDATE
    crowd_qna_reply_#{crowdId}
SET
    is_deleted = true
WHERE
    crowd_id = #{crowdId}
