mapper.update:
UPDATE
    crowd_reward_#{crowdId}
SET
    reward_title = #{rewardTitle},
    reward_content = #{rewardContent},
    reward_amount = #{rewardAmount},
    reward_limit = #{rewardLimit},
    reward_left = #{rewardLeft}
WHERE reward_id = #{rewardId}

UPDATE
    crowd_reward_#{crowdId}
SET
    reward_left = #{rewardLeft}
WHERE
    reward_id = #{rewardId}

mapper.delete:
UPDATE crowd_reward_#{crowdId}
SET is_deleted = true
WHERE reward_id = #{rewardId}

UPDATE
    crowd_reward_#{crowdId}
SET
    is_deleted = true
WHERE
    crowd_id = #{crowdId}
