mapper.select:
SELECT
    u_like_id as uLikeId,
    project_id as projectId,
    project_type as projectType
FROM
    user_like_${userEmail}
WHERE
    project_id = #{projectId}
AND
    project_type = #{projectType}

mapper.delete:
DELETE
FROM
    user_like_${userEmail}
WHERE
    project_id = #{projectId}
AND
    project_type = #{projectType}
